import React from "react";

import generateClassModifiers from "../../../utilities/generateClassModifiers"

function Buttons (props) {
  const {setWorkLength, 
         setBreakLength,
         setMinutes, 
         setSeconds, 
         isOn, 
         setIsOn,
         isColorFliped,
         isWorkFinished, 
         setIsWorkFinished, 
         handlePlay, 
         handleReset} = props

  const playOrPauseIcon = isOn ? 
                      <img className="pause" src="data:image/svg+xml;base64," /> :
                      <img className="play" src="data:image/svg+xml;base64," />
 
  const playBtn = generateClassModifiers(isColorFliped, isWorkFinished, "play-Pause--background-green", "play-Pause--background-red")
  const resetBtn = generateClassModifiers(isColorFliped, isWorkFinished, "reset--font-green", "reset--font-red")

  return (
    <>
      <button className={`play-Pause ${playBtn}`} onClick={()=>handlePlay(isOn, setIsOn)}>
        {playOrPauseIcon}
      </button>
      <button className={`reset ${resetBtn}`} onClick={()=>handleReset(setWorkLength, setBreakLength, setMinutes, setSeconds, setIsOn, setIsWorkFinished)}>RESET</button>
    </>
  )
} 

export default Buttons